class Record {
	constructor(source) {
		this.source = source;
	}
}

class Obj {
	constructor(recordNo) {
		this.recordNo = recordNo;
	}
}

class ParkingPlace extends Obj {
	constructor(recordNo, number, building, area) {
		super(recordNo);
		this.number = number;
		this.building = building;
		this.area = area;
	}
}

class Apartment extends Obj {
	constructor(recordNo, objectType, number, building, floor, section, area) {
		super(recordNo);
		this.objectType = objectType;
		this.number = number;
		this.building = building;
		this.floor = floor;
		this.section = section;
		this.area = area;
	}
}

class NonResidentialPremise extends Obj {
	constructor(recordNo, objectType, number, building, section, area) {
		super(recordNo);
		this.objectType = objectType;
		this.number = number;
		this.building = building;
		this.section = section;
		this.area = area;
	}
}

module.exports = { Record, Obj, ParkingPlace, Apartment, NonResidentialPremise };
