var View = require("app/views/View");

class DataView extends View
{
	constructor() {
		super(...arguments);
		this.tabs = this.addChild(new TabControl());
		this.tabs.addTab(Tab.List, "Список");
		this.tabs.addTab(Tab.Diagram, "Шахматка");
		this.tabs.addTab(Tab.Sql, "SQL");

		this.dataList = this.addChild(new DataListView());
		this.dataDiagram = this.addChild(new DataDiagramView());
		this.dataSqlView = this.addChild(new DataSqlView());
	}
	getHtml() {
		return `
			<div class="DataView" id="${this.uid}">
				${this.tabs.getHtml()}
				${this.dataList.getHtml()}
				${this.dataDiagram.getHtml()}
				${this.dataSqlView.getHtml()}
			</div>
		`;
	}
}

module.exports = DataView;

var Tab = {
	List: 1,
	Diagram: 2,
	SQL: 3,
};

var TabControl = require("app/views/TabControl");
var DataListView = require("app/views/DataListView");
var DataDiagramView = require("app/views/DataDiagramView");
var DataSqlView = require("app/views/DataSqlView");
