var View = require("app/views/View");

class MainView extends View
{
	constructor(model) {
		super();
		this.loadView = this.addChild(new LoadView(model));
		this.dataView = this.addChild(new DataView(model));
	}
	getHtml() {
		return `
			<div class="MainView" id="${this.uid}">
				${this.loadView.getHtml()}
				${this.dataView.getHtml()}
			</div>
		`;
	}
}

module.exports = MainView;

var LoadView = require("app/views/LoadView");
var DataView = require("app/views/DataView");
